

def initializeCountDicts():
    """Creates two dictionaries. codonCountD is keyed by codon (using the
    61 non-stop codons). twoCodonCountD is keyed by codon pairs
    (represented by 6 bp strings). All values in each are initialized
    to 0.
    """
    # get a list of all codon pairs
    def codonCombos(numCodons,codonL):
        """Return all possible codon combos numCodons long."""
        if numCodons==0:
            return [""]
        else:
            outL=[]
            for codon in codonL:
                for combo in codonCombos(numCodons-1,codonL):
                    outL.append(codon+combo)
            return outL

    codonPairL=codonCombos(2,codonL)
    
    # create dicts
    codonCountD={}
    for codon in codonL: codonCountD[codon]=0
    twoCodonCountD={}
    for codonPair in codonPairL: twoCodonCountD[codonPair]=0
    return codonCountD,twoCodonCountD

def printProbs(probD,formatString):
    """Print dictionary of conditional probabilities, organized according to
    the prefixes of its keys. Use precision given in formatString
    (e.g. '.3f' etc.).
    """
    S=set()
    for key in probD.keys():
        S.add(key[:3])
    prefixes=sorted(list(S))
    print("Matrix of conditional probabilities:\n---")
    for prefix in prefixes:
        i=0
        for codon in codonL:
            print(prefix+codon+":"+format(probD[prefix+codon],formatString),end=' ')
            if i%6==5: print()
            i+=1
            
        print("\n---")

